    labels: [
        {
            key: "key 1",
            value: "value 1",
        },
        {
            key: "key 2",
            value: "value 2",
        },
    ],
});
const testDatabaseUsers = mongodbatlas.getDatabaseUsersOutput({
    projectId: testDatabaseUser.projectId,
});
