    clusterType: "REPLICASET",
    replicationSpecs: [{
        numShards: 1,
        regionsConfigs: [{
            regionName: "US_EAST_1",
            electableNodes: 3,
            priority: 7,
            readOnlyNodes: 0,
        }],
    }],
    cloudBackup: true,
    autoScalingDiskGbEnabled: true,
    providerName: "AWS",
    providerInstanceSizeName: "M40",
});
const testClusters = mongodbatlas.getClustersOutput({
    projectId: testCluster.projectId,
});
