    name: "<CLUSTER-NAME>",
    clusterType: "GEOSHARDED",
    backupEnabled: true,
    replicationSpecs: [
        {
            zoneName: "Zone 1",
            regionConfigs: [{
                electableSpecs: {
                    instanceSize: "M30",
                    nodeCount: 3,
